import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext(null);
export const useAuth = () => useContext(AuthContext);

const API_URL = import.meta?.env?.VITE_API_BASE_URL || process.env.REACT_APP_API_URL || "http://localhost:3001";

export function AuthProvider({ children }) {
  const [token, setToken] = useState(() => localStorage.getItem("token"));

  useEffect(() => {
    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [token]);

  async function login({ email, password }) {
    // AJUSTE a rota conforme seu server (/login, /auth, /sessions, etc.)
    const res = await fetch(`${API_URL}/login`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    if (!res.ok) {
      let msg = "Credenciais inválidas";
      try { const body = await res.json(); msg = body?.message || msg; } catch {}
      throw new Error(msg);
    }

    const data = await res.json(); // espera { token: "..." }
    if (!data?.token) throw new Error("Resposta sem token");
    setToken(data.token);
  }

  function logout() {
    setToken(null);
  }

  return (
    <AuthContext.Provider value={{ token, isAuthenticated: !!token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
 export default AuthContext;
